$(document).ready(function(){
	jQuery('.portfolio-link').each(function(){
		var img_src = jQuery(this).find('img').attr('src');
		jQuery(this).css('background-image','url(' + img_src + ')');
		jQuery(this).attr('data-image',img_src);
	});
	jQuery('.portfolio-link').click(function(){
		//modal content setup
		var img_src = jQuery(this).find('img').attr('src');
		var port_text = jQuery(this).parent().find('.portfolio-caption').html();
		jQuery('#portfolioModal1 .modal-body').html('');
		jQuery('#portfolioModal1 .modal-body').prepend('<img src="' + img_src + '">');
		jQuery('#portfolioModal1 .modal-body').prepend(port_text);
	});
	jQuery('.portfolio-item').each(function(){
		jQuery(this).addClass('active');
	});
});

$(window).load(function(){
	var $grid = $('.portfolio-grid').isotope({
	  itemSelector: '.portfolio-item'
	});
	jQuery('.port-filter').click(function(e){
		e.preventDefault();
		jQuery('.port-filter').parent().removeClass('active');
		jQuery(this).parent().addClass('active');
		var category = jQuery(this).attr('data-category');
		jQuery('.portfolio-item').removeClass('active');
		$grid.isotope({ filter: category });
		jQuery(category).addClass('active');
	});
	jQuery('a[data-category=".feat-fil"]').click();
});